import React, { useState } from 'react';
import axios from 'axios';
import './Search.css'; // Ensure you have a Search.css file in your project

function Search() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.post('http://localhost:5000/', { query });
      setResults(response.data);
    } catch (error) {
      console.error("Failed to fetch search results:", error);
      setResults([]);
    }
  };

  return (
    <div className="search-wrapper">
      <input 
        className="search-input"
        type="text" 
        value={query} 
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter search terms..."
      />
      <button className="search-button" onClick={handleSearch}>Search</button>
      <div className="results-container">
        {results.map((result, index) => (
          <div key={index} className="result-item">
            <span className="result-title">{result.title}</span>
            {/* Here we use an anchor tag instead of a button */}
            <a href={result.url} target="_blank" rel="noopener noreferrer" className="sample-button">
              Read {result.fileType} File
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Search;
